class Solution:
    def countNegatives(self, grid: List[List[int]]) -> int:
        ROWS, COLS = len(grid), len(grid[0])
        negatives = 0
        for row in range(ROWS):
            col = COLS - 1
            while col >= 0 and grid[row][col] < 0:
                col -= 1
            negatives += COLS - (col + 1)
        return negatives

