class Solution {
    public int minFallingPathSum(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;

        int memo[][] = new Integer[m][n];

        int ans = Integer.MAX_VALUE;

        for (int j = 0; j < n; j++) {
            ans = Math.min(ans, minFallingPathSum_Tabulation(grid, 0, j));
        }
        return ans;
    }

    public int minFallingPathSum_Tabulation(int[][] grid, int i, int j) {
        if (i == grid.length - 1)
            return grid[i][j];

        if (memo[i][j] != null)
            return memo[i][j];

        int lowerLeftSum = Integer.MAX_VALUE;
        int lowerRightSum = Integer.MAX_VALUE;

        for (int col = Math.max(0, j - 1); col <= j; col++) {
            lowerLeftSum = Math.min(lowerLeftSum, minFallingPathSum_Tabulation(grid, i + 1, col));
        }

        for (int col = j + 1; col < grid[0].length; col++) {
            lowerRightSum = Math.min(lowerRightSum, minFallingPathSum_Tabulation(grid, i + 1, col));
        }

        return memo[i][j] = Math.min(Math.min(lowerLeftSum, lowerRightSum), grid[i][j]);
    }
}

