        Map<Integer, Integer> map = new TreeMap<>();
        int l1 = nums1.length;
        int l2 = nums2.length;

        for (int i = 0; i < l1; i++) {
            if (!map.containsKey(nums1[i][0])) {
                map.put(nums1[i][0], nums1[i][1]);
            }
            else {
                map.put(nums1[i][0], (map.get(nums1[i][0]) + nums1[i][1]));
