                map.put(nums2[i][0], nums2[i][1]);
            }
            else {
                map.put(nums2[i][0], (map.get(nums2[i][0]) + nums2[i][1]));
            }

        }

        int count = 0;
        int[][] ans = new int[map.size()][2];

        for (Map.Entry<Integer, Integer> entry : map.entrySet()) {
            ans[count][0] = entry.getKey();
            ans[count][1] = entry.getValue();
            count++;
        }

        return ans;
    }
}

