class Solution:
    def numOfPairs(self, nums: List[str], target: str) -> int:
        d = defaultdict(int)
        for char in nums:
            d[char] += 1

        arr = []
        for char in target:
            arr.append(char)

        pairs = 0
        num = ""
        while len(arr) > 1:
            num += arr.pop()
            findNum = "".join(arr)
            if num[::-1] not in d or findNum not in d:
                continue

            c1 = d[num[::-1]]
            d[num[::-1]] -= c1
            c2 = d[findNum]
            pairs += c1 * c2
        return pairs

