class Solution:
    def frogPosition(self, n: int, edges: [[int]], t: int, target: int) -> float:
        if not edges and target == 1: 
            return 1
        if not edges:
            return 0

        graph = {}
        for u, v in edges:
            if u not in graph:
                graph[u] = []
            if v not in graph:
                graph[v] = []
            graph[u].append(v)
            graph[v].append(u)

        visited = [False]*(n+1)
        queue = [(1, 1.0)]
        for _ in range(t):
            queue = [(u, p) for u, p in queue if not visited[u]]
            for u, p in list(queue):
                visited[u] = True
                for v in graph[u]:
                    if not visited[v]: 
                        heapq.heappush(queue, (v, p/len(graph[u])))
        return next((p for u, p in queue if u == target), 0)

