class Solution:
    def moveFinal(self, stones: List[int]) -> List[int]:
        stones.sort()
        stone_len = len(stones)
        move_penultimate = stones[-2] - stones[0] - stone_len + 2
        move_final = stones[-1] - stones[1] - stone_len + 2 
        most_moves = min(move_penultimate, move_final)
        if most_moves == 0:
            return [most_moves]
        max_legal_moves = 0 
        for starting_index in range(stone_len):
            max_legal_moves = min(max(max_legal_moves, stone_len - starting_index), most_moves) 
        return [stone_len-max_legal_moves, most_moves]

