class Solution:
    def splitListToParts(self, head: Optional[ListNode], k: int) -> List[Optional[ListNode]]:
        length = 0
        ptr = head
        while(ptr) :
            length += 1
            ptr = ptr.next

        arrange = []
        maxi = length//k 
        remain = length%k

        for i in range(k) :
            if remain :
                arrange.append(maxi + 1)  
                remain -= 1
            else:
                arrange.append(maxi)

        l = []
        j = 0
        ptr = head 
        while(ptr) :
            q = ptr 
            ptr = ptr.next 
            if len(l) == arrange[j] :
                q.next = None
                l.append(head)
                head = ptr 
                j += 1

        for _ in range(len(arrange), k):
            l.append(None)
        return l

