class Solution:
    def stoneGameVI(self, a: List[int], b: List[int]) -> int:
        combines = sorted(((a[i] + c[i], i) for i in range(len(a))), reverse=True)
        alicePoints = sum(combines[i][1] for i in (i for i in range(len(a)) if i % 2 == 0))
        bobPoints = sum(a[i] + b[i] - combines[i][0] for i in (i for i in range(len(a)) if i % 2 != 0))
        return 1 if alicePoints > bobPoints else -1 if alicePoints < bobPoints else 0

