        if len(dist) >= hour: return -1
        left, right = 1, max(dist) + 1
        while left < right:
            mid = (left + right) // 2
            if sum([math.ceil(i / mid) for i in dist]) + math.ceil(dist[-1] / mid) <= hour:
                right = mid
            else:
                left = mid + 1
        return left

