class Solution:
    def moveFinal(self, stones: List[int]) -> List[int]:
        stones.sort()
        stone_len = len(stones)
        move_penultimate = stones[-2] - stones[0] - 1
        move_final = stones[-1] - stones[1] - 1 
        most_moves = max(move_penultimate, move_final)
        if move_penultimate == move_final:
            return [most_moves]
        min_legal_moves = most_moves
        starting_index = 0
        for index in range(1, stone_len):
            while stones[starting_index] <= stones[index] - stone_len:
                starting_index += 1
            max_legal_moves = min(max(max_legal_moves, index - starting_index + 1), most_moves) 
        return [stone_len - max_legal_moves, most_moves]

