class Solution:
    def smallestString(self, s: str) -> str:
        check = set(s)
        if len(check) == 1 and list(check)[0] != 'a':
            return s
        contiguous, change, ct1, ct2 = True, False, 0, 0
        for i in s:
            if i == 'a': ct1 += 1
            else: break
        ans = ''
        ans += 'a' * ct1
        s = s[ct1:]
        for cha in s:
            ct2 += 1
            if cha != 'a':
                ans += chr(ord(cha) - 1)
                change = True
                continue
            else:
                ans += cha
                if change:
                    contiguous = False
        return ans + s[ct2:] if change else ans[:-1] + 'z'

