class Solution:
    def findSubstring(self, s: str, words: List[str]) -> List[int]:
        wlen = len(words[0])
        slen = wlen * len(words)
        track = dict()

        occ = collections.Counter(words)

        for k in range(len(s) - slen + 1):
            track.clear()
            for i in range(k, k + slen, wlen):
                w = s[i:i+wlen]
                if w not in words:
                    break
                track[w] = track.get(w, 0) + 1
            else:
                if track == occ:
                    res.append(k)
        return res

