class Solution:
    def splitListToParts(self, head: Optional[ListNode], k: int) -> List[Optional[ListNode]]:
        curr = head
        length = 0
        while curr:
            length += 1
            curr = curr.next

        quotient, remainder = divmod(length, k)

        result = []
        prev = None
        for _ in range(k):
            if not prev:
                prev = head
                head = head.next
            else:
                prev.next = None
                prev = head

            if remainder > 0:
                remainder -= 1
            else:
                quotient += 1

        result.append(head)
        while head:
            length = 0
            temp = head
            while temp:
                length += 1
                temp = temp.next
            for _ in range(length - 1):
                head = head.next

            if remainder > 0:
                remainder -= 1
            else:
                quotient -= 1

            result.append(head.next)
            head.next = None

        while len(result) < k:
            result.append(None)

        return result

