class Solution:
    def findMedianSortedArrays(self, nums1: List[int], nums2: List[int]) -> float:
        p1 = 0
        p2 = 0
        new = []

        while p1 < len(nums1) and p2 < len(nums2):
            if nums1[p1] < nums2[p2]:
                new.append(nums1[p1])
                p1 += 1
            else:
                new.append(nums2[p2])
                p2 += 1
        new.extend(nums1[p1:])
        new.extend(nums2[p2:])
        n = len(new)
        if n % 2 == 0:
            median = (new[n//2 - 1] + new[n//2]) / 2
        else:
            median = float(new[n//2])
        return median
