    def smallestFromLeaf(self, root: Optional[TreeNode]) -> str:
        self.result = ""

        #to check if a node is leaf node, return true if it is
        def isLeaf(node):
            if node:
                return not node.left and not node.right
            return False

        # recursive function to traverse
        def traverse(path, node):
            if not node: return

            path += chr(ord('a') + node.val)

            if isLeaf(node) and path[::-1] < self.result:
                self.result = path[::-1]
            else:
                traverse(path, node.left)
                traverse(path, node.right)

        traverse("", root)

