class Solution:
    def maxPower(self, stations: List[int], r: int, k: int) -> int:
        start, end = min(stations), sum(stations) + k
        while start < end:
            mid = (start + end + 1) // 2
            if self.check(stations, r, k, mid):
                start = mid
            else:
                end = mid - 1
        return start

    def check(self, stations, r, k, target):
        n = len(stations)
        power = 0
        for i in range(r + 1):
            power += stations[i]
        if power < target:
            return False
        newStations = defaultdict(int)
        while True:
